// Parse WhatsApp message and extract command
const messageBody = $input.first().json.Body.trim().toUpperCase();
const fromNumber = $input.first().json.From;
const messageId = $input.first().json.MessageSid;

// Command patterns
const listPattern = /^LIST\s+(.+)$/;
const deletePattern = /^DELETE\s+(.+)$/;
const movePattern = /^MOVE\s+(.+?)\s+(.+)$/;
const summaryPattern = /^SUMMARY\s+(.+)$/;
const helpPattern = /^HELP$/;

let command = null;
let params = {};

if (listPattern.test(messageBody)) {
  const match = messageBody.match(listPattern);
  command = 'LIST';
  params.folderPath = match[1];
} else if (deletePattern.test(messageBody)) {
  const match = messageBody.match(deletePattern);
  command = 'DELETE';
  params.filePath = match[1];
} else if (movePattern.test(messageBody)) {
  const match = messageBody.match(movePattern);
  command = 'MOVE';
  params.sourcePath = match[1];
  params.destinationPath = match[2];
} else if (summaryPattern.test(messageBody)) {
  const match = messageBody.match(summaryPattern);
  command = 'SUMMARY';
  params.folderPath = match[1];
} else if (helpPattern.test(messageBody)) {
  command = 'HELP';
} else {
  command = 'UNKNOWN';
}

return {
  json: {
    originalMessage: messageBody,
    fromNumber,
    messageId,
    command,
    params,
    timestamp: new Date().toISOString()
  }
};

// Get folder contents for summarization
const folderPath = $input.first().json.params.folderPath;

// This will be used to fetch files from the folder
return {
  json: {
    folderPath: folderPath === '/' ? 'root' : folderPath,
    operation: 'summary'
  }
};

// Process files for summarization
const files = $input.all().map(item => item.json);
const summaryFiles = [];

for (const file of files) {
  // Only process text-based files
  if (file.mimeType && (
    file.mimeType.includes('text/') ||
    file.mimeType.includes('application/pdf') ||
    file.mimeType.includes('application/vnd.google-apps.document') ||
    file.mimeType.includes('application/vnd.openxmlformats-officedocument')
  )) {
    summaryFiles.push({
      id: file.id,
      name: file.name,
      mimeType: file.mimeType,
      size: file.size,
      modifiedTime: file.modifiedTime
    });
  }
}

return summaryFiles.map(file => ({ json: file }));

