// Format responses for WhatsApp
const inputData = $input.first().json;
const command = $('Command Parser').first().json.command;
let responseMessage = '';

switch (command) {
  case 'LIST':
    if (inputData.files && inputData.files.length > 0) {
      responseMessage = `üìÅ Files in folder:\n\n`;
      inputData.files.forEach((file, index) => {
        responseMessage += `${index + 1}. ${file.name}\n`;
        responseMessage += `   Type: ${file.mimeType}\n`;
        responseMessage += `   Size: ${file.size ? Math.round(file.size / 1024) + 'KB' : 'N/A'}\n\n`;
      });
    } else {
      responseMessage = 'üìÅ No files found in the specified folder.';
    }
    break;

  case 'DELETE':
    if (inputData.id) {
      responseMessage = 'üóëÔ∏è File deleted successfully!';
    } else {
      responseMessage = '‚ùå Failed to delete file. Please check the file path.';
    }
    break;

  case 'MOVE':
    if (inputData.id) {
      responseMessage = 'üìÅ File moved successfully!';
    } else {
      responseMessage = '‚ùå Failed to move file. Please check the file and destination paths.';
    }
    break;

  case 'SUMMARY':
    if (inputData.message) {
      responseMessage = `üìÑ Document Summary:\n\n${inputData.message}`;
    } else {
      responseMessage = '‚ùå No summarizable documents found in the folder.';
    }
    break;

  case 'HELP':
    responseMessage = `ü§ñ WhatsApp Google Drive Assistant\n\nAvailable commands:\n\nüìã LIST /folder/path - List files in folder\nüóëÔ∏è DELETE /file/path - Delete a file\nüìÅ MOVE /source /destination - Move file\nüìÑ SUMMARY /folder/path - Summarize documents\n‚ùì HELP - Show this help\n\nExample: LIST /ProjectX`;
    break;

  case 'UNKNOWN':
  default:
    responseMessage = '‚ùì Unknown command. Send HELP to see available commands.';
}

return {
  json: {
    message: responseMessage,
    originalCommand: command,
    timestamp: new Date().toISOString()
  }
};

// Audit logging for all operations
const commandData = $('Command Parser').first().json;
const responseData = $('Format WhatsApp Response').first().json;

const logEntry = {
  timestamp: new Date().toISOString(),
  messageId: commandData.messageId,
  fromNumber: commandData.fromNumber,
  command: commandData.command,
  params: commandData.params,
  success: !responseData.message.includes('‚ùå'),
  response: responseData.message.substring(0, 100) + (responseData.message.length > 100 ? '...' : '')
};

// Log to console (in production, this would go to a database)
console.log('Audit Log:', JSON.stringify(logEntry, null, 2));

return {
  json: logEntry
};

// Error handler for failed operations
const error = $input.first().json.error || 'Unknown error occurred';
const commandData = $('Command Parser').first().json;

const errorMessage = `‚ùå Error: ${error.message || error}\n\nPlease check your command and try again. Send HELP for available commands.`;

return {
  json: {
    message: errorMessage,
    error: true,
    timestamp: new Date().toISOString()
  }
};
